using System.Collections.Concurrent;

using AzerothCore.Game.Server;
using AzerothCore.Singleton;

namespace AzerothCore.Game;

public sealed class OpcodeTable : Singleton<OpcodeTable>
{
    private readonly ConcurrentDictionary<Opcodes, ClientOpcodeHandler?> _clientPacketTable = new();

    private OpcodeTable() { }

    internal ClientOpcodeHandler? this[Opcodes opcode]
    {
        get
        {
            if (_clientPacketTable.ContainsKey(opcode))
            {
                return _clientPacketTable[opcode];
            }
            else
            {
                return null;
            }
        }

        set
        {
            _clientPacketTable[opcode] = value;
        }
    }

    private void DEFINE_HANDLER(Opcodes opcode, SessionStatus status, PacketProcessing processing, OpcodeHandlerFunction func)
    {
        _clientPacketTable[opcode] = new WorldPacketHandler(status, processing, func);
    }

    private void DEFINE_SERVER_OPCODE_HANDLER(Opcodes opcode, SessionStatus status)
    {
        _clientPacketTable[opcode] = new PacketHandler(status);
    }

